import React, { useEffect, useRef } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import { OrbitControls, useGLTF, Bounds } from '@react-three/drei';

function CenterModel({ scene }) {
  const { camera } = useThree();
  const bounds = useRef();

  useEffect(() => {
    if (bounds.current) {
      // Calculate the bounding box and fit the view
      bounds.current.refresh(scene).clip().fit();
      
      // Get the size of the model
      const size = bounds.current.getSize();
      const maxDimension = Math.max(size.x, size.y, size.z);
      
      // Adjust camera position based on model size
      const cameraDistance = maxDimension * 2.5;
      camera.position.set(0, 0, cameraDistance);
      camera.lookAt(0, 0, 0);
      camera.updateProjectionMatrix();
    }
  }, [scene, camera]);

  return (
    <Bounds ref={bounds} margin={1.2}>
      <primitive object={scene} />
    </Bounds>
  );
}

function ModelViewer({ modelId }) {
  const { scene } = useGLTF(`http://localhost:5000/api/models/${modelId}`);
  
  // Reset rotation and position of the model
  scene.rotation.set(0, 0, 0);
  scene.position.set(0, 0, 0);

  return (
    <div className="w-full h-full">
      <Canvas
        camera={{ fov: 45 }}
        style={{ width: '100vw', height: '100vh' }}
      >
        <ambientLight intensity={0.5} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <CenterModel scene={scene} />
        <OrbitControls 
          enablePan={true} 
          enableZoom={true}
          minDistance={0.5}
          maxDistance={20}
        />
      </Canvas>
    </div>
  );
}

export default ModelViewer;
